INSERT INTO public.info (id, info_name, info_text, city_id, category_id, sort_number) VALUES (1393, 'Wstęp na występy', '<span style="font-weight: normal; background-color: rgba(0, 0, 255, 0);" id="docs-internal-guid-d7693a27-7fff-5607-fc6a-1c323e80bdb9"><p dir="ltr" style="line-height: 1.656; margin-top: 0pt; margin-bottom: 0pt; padding: 0pt 0pt 6pt;"><span style="font-size: 18pt; font-family: Ubuntu, sans-serif; color: rgb(0, 51, 153); font-weight: 700; font-style: normal; font-variant: small-caps; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">KIBICUJ KULTURALNIE:&nbsp;</span></p><p dir="ltr" style="line-height:1.3800000000000001;margin-top:0pt;margin-bottom:6pt;"><span style="font-size: 14pt; font-family: Ubuntu, sans-serif; color: rgb(0, 0, 0); font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">Na odysejowe spektakle </span><span style="font-size: 14pt; font-family: Ubuntu, sans-serif; color: rgb(0, 0, 0); font-weight: 700; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">wstęp jest wolny</span><span style="font-size: 14pt; font-family: Ubuntu, sans-serif; color: rgb(0, 0, 0); font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">.</span></p><p dir="ltr" style="line-height:1.3800000000000001;margin-top:0pt;margin-bottom:6pt;"><span style="font-size: 14pt; font-family: Ubuntu, sans-serif; color: rgb(0, 0, 0); font-weight: 700; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">Zakazane jest wnoszenie do sal jedzenia i napojów. </span><span style="font-size: 14pt; font-family: Ubuntu, sans-serif; color: rgb(0, 0, 0); font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">Za z