INSERT INTO public.info (id, info_name, info_text, city_id, category_id, sort_number) VALUES (1396, 'Zasady zachowania', '<span style="font-weight: normal;" id="docs-internal-guid-bfb15133-7fff-15bf-778a-0a1b5cbb1f40"><p dir="ltr" style="line-height: 1.656; margin-top: 0pt; margin-bottom: 0pt; padding: 0pt 0pt 6pt;"><span style="font-size: 18pt; font-family: Ubuntu, sans-serif; color: rgb(0, 51, 153); font-weight: 700; font-style: normal; font-variant: small-caps; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">RESPEKTUJ REGUŁY:&nbsp;</span></p><p dir="ltr" style="line-height: 1.38; margin-top: 0pt; margin-bottom: 6pt;"><span style="font-size: 14pt; font-family: Ubuntu, sans-serif; color: rgb(0, 0, 0); font-weight: 700; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">Obowiązuje zakaz zamawiania jedzenia na dowóz, zakaz wynoszenia z bufetu napojów i/lub posiłków, a także zakaz konsumpcji na korytarzach. </span><span style="font-size: 14pt; font-family: Ubuntu, sans-serif; color: rgb(0, 0, 0); font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">Za złamanie zakazu grozi kara finansowa!&nbsp;</span></p><p dir="ltr" style="line-height: 1.38; margin-top: 0pt; margin-bottom: 6pt;"><span style="font-size: 14pt; font-family: Ubuntu, sans-serif; color: rgb(0, 0, 0); font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">Na całym terenie PPNT i Areny </span><span style="font-size: 14pt; font-family: Ubuntu, sans-serif; color: rgb(0, 0, 0); font-weight: 700; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">zakazane jest palenie tytoniu oraz e-papierosów</span><span style="font-size: 14pt; font-family: Ubuntu, sans-serif; color: rgb(0, 0, 0); font-weight: 400; font-st